:"BalancedVault","version":1},"userdoc":{"kind":"user","methods":{"approve(address,uint256)":{"notice":"Sets `amount` as the allowance of `spender` over the caller's shares"},"balanceOf(address)":{"notice":"Number of shares held by `account`"},"claim(address)":{"notice":"Claims all claimable assets for account, sending assets to account"},"convertToAssets(uint256)":{"notice":"Converts a given amount of shares to assets"},"convertToShares(uint256)":{"notice":"Converts a given amount of assets to shares"},"decimals()":{"notice":"Returns the decimals places of the share token"},"deposit(uint256,address)":{"notice":"Deposits `assets` assets into the vault, returning shares to `account` after the deposit settles."},"initialize(string,string)":{"notice":"Initializes the contract state"},"maxDeposit(address)":{"notice":"The maximum available deposit amount"},"maxRedeem(address)":{"notice":"The maximum available redeemable amount"},"redeem(uint256,address)":{"notice":"Redeems `shares` shares from the vault"},"sync()":{"notice":"Rebalances the collateral and position of the vault without a deposit or withdraw"},"totalAssets()":{"notice":"The total amount of assets currently held by the vault"},"totalSupply()":{"notice":"The total amount of shares currently issued"},"totalUnclaimed()":{"notice":"Total unclaimed assets in vault"},"transfer(address,uint256)":{"notice":"Moves `amount` shares from the caller's account to `to`"},"transferFrom(address,address,uint256)":{"notice":"Moves `amount` shares from `from to `to`"},"unclaimed(address)":{"notice":"`account`'s unclaimed assets"}},"notice":"ERC4626 vault that manages a 50-50 position between long-short markets of the same payoff on Perennial.","version":1}},"settings":{"compilationTarget":{"@equilibria/perennial-vaults/contracts/BalancedVault.sol":"BalancedVault"},"evmVersion":"london","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":true,"runs":200},"remappings":[]},"sources":{"@equilibria/emptyset-batcher/interfaces/IBatcher.sol":{"content":"//SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.13;

import "@equilibria/root/number/types/UFixed18.sol";
import "@equilibria/root/token/types/Token18.sol";
import "@equilibria/root/token/types/Token6.sol";
import "../interfaces/IEmptySetReserve.sol";

interface IBatcher {
    event Wrap(address indexed to, UFixed18 amount);
    event Unwrap(address indexed to, UFixed18 amount);
    event Rebalance(UFixed18 newMinted, UFixed18 newRedeemed);
    event Close(UFixed18 amount);

    error BatcherNotImplementedError();
    error BatcherBalanceMismatchError(UFixed18 oldBalance, UFixed18 newBalance);

    function RESERVE() external view returns (IEmptySetReserve); // solhint-disable-line func-name-mixedcase
    function USDC() external view returns (Token6); // solhint-disable-line func-name-mixedcase
    function DSU() external view returns (Token18); // solhint-disable-line func-name-mixedcase
    function totalBalance() external view returns (UFixed18);
    function wrap(UFixed18 amount, address to) external;
    function unwrap(UFixed18 amount, address to) external;
    function rebalance() external;
}
